var showMenu = false;

document.getElementById("buttonMenu").onclick = function () {
  if (showMenu) {
    document.body.classList.remove("show-navigation");
    showMenu = false;
  } else {
    document.body.classList.add("show-navigation");
    showMenu = true;
  }
};

var calculatorBtn = false;
document.getElementById("calculatorBtn").onclick = function () {
  if (calculatorBtn) {
    document.getElementById("calculatorBtn").style.backgroundColor = "#3671e9";
    calculatorBtn = false;
  } else {
    document.getElementById("calculatorBtn").style.backgroundColor = "red";
    calculatorBtn = true;
  }
};
